mapper.select:
select orderation_no orderationNo,ordererName,ordererCount,password
from orderer
where password = #{password} and ordererName = #{orderName}

select orderation_no orderationNo,ordererName,ordererCount,password
from orderer
where password = #{value}

 select er.orderation_no, er.ordererName,er.ordererCount,er.password,o.orderation_no,o.product_name,o.buyerName,o.phone1,o.phone2,o.phone3,o.orderation_address,o.postalNumber,o.code,o.orderation_price ,o.product_amount,o.imageName
from orderer er, orderation o
where er.orderation_no = o.orderation_no and password = #{value}

 select er.orderation_no, er.ordererName,er.ordererCount,er.password,o.orderation_no,o.product_name,o.buyerName,o.phone1,o.phone2,o.phone3,o.orderation_address,o.postalNumber,o.code,o.orderation_price ,o.product_amount,o.imageName
from orderer er, orderation o
where er.orderation_no = o.orderation_no and o.code = #{value}
