mapper.update:
update orderation
set code = #{code}
where orderation_no = #{orderationNo}

mapper.select:
select orderation_no orderationNo,product_name productName,buyerName,phone1,phone2,phone3,orderation_address orderationAddress,postalNumber,code,orderation_price orderationPrice,product_amount productAmount ,imageName
from orderation 
where orderation_no = #{value}
